INSERT INTO cards (type, title, subtitle, content, points_reward, is_active) VALUES
('journal_prompt', 'Gratitude List', NULL, '{"journal_prompt_text":"Write down 10 things you''re grateful for today. Be specific — not just ''family'' but ''the way my sister texted me just to check in.''","journal_prompt_category":"gratitude"}', 5, true),
('journal_prompt', 'Letter to God', NULL, '{"journal_prompt_text":"Write a letter to God. Tell Him exactly how you''re feeling right now — no filter, no religious language. Just honest conversation.","journal_prompt_category":"prayer"}', 5, true),
('journal_prompt', 'Your Testimony', NULL, '{"journal_prompt_text":"In a few paragraphs, write your testimony. How did you come to faith? What has God done in your life? Writing it down helps you remember His faithfulness.","journal_prompt_category":"reflection"}', 5, true),
('journal_prompt', 'Letting Go', NULL, '{"journal_prompt_text":"What are you holding onto that you need to release? Write it down. Name the fear, the hurt, the bitterness. Then write, ''I choose to let this go.''","journal_prompt_category":"healing"}', 5, true),
('journal_prompt', 'Dream Big', NULL, '{"journal_prompt_text":"If God could do anything in your life in the next year, what would you ask for? Don''t limit yourself. Dream as big as He is.","journal_prompt_category":"vision"}', 5, true),
('journal_prompt', 'Identity in Christ', NULL, '{"journal_prompt_text":"Write down 5 things God says about you (loved, chosen, forgiven, etc.). Next to each one, write a lie the world tells you that contradicts it. Choose truth.","journal_prompt_category":"identity"}', 5, true),
('journal_prompt', 'The Hard Question', NULL, '{"journal_prompt_text":"Is there a question you''ve been avoiding asking God? Write it down. It''s okay to have questions — even hard ones. He can handle them.","journal_prompt_category":"faith"}', 5, true),
('journal_prompt', 'Today''s Blessing', NULL, '{"journal_prompt_text":"What was the best moment of your day today? Relive it in writing. Thank God for it. Train your eyes to see blessings, even small ones.","journal_prompt_category":"gratitude"}', 5, true),
('journal_prompt', 'Forgiveness Journal', NULL, '{"journal_prompt_text":"Is there someone you need to forgive? Write down what they did, how it made you feel, and then write, ''I choose to forgive [name] because Christ forgave me.''","journal_prompt_category":"forgiveness"}', 5, true),
('journal_prompt', 'Psalm Writing', NULL, '{"journal_prompt_text":"Write your own psalm. Start with praise, move to your current struggle, and end with a declaration of trust. The Psalms are your template.","journal_prompt_category":"worship"}', 5, true),
('journal_prompt', 'Life Verse', NULL, '{"journal_prompt_text":"Do you have a life verse? If so, write about why it means so much to you. If not, spend time finding one and journal about why it resonates.","journal_prompt_category":"scripture"}', 5, true),
('journal_prompt', 'Where I Was vs Where I Am', NULL, '{"journal_prompt_text":"Compare where you were one year ago to where you are today. What has changed? What has God done? Growth is easier to see looking backwards.","journal_prompt_category":"reflection"}', 5, true),
('journal_prompt', 'Prayer Requests', NULL, '{"journal_prompt_text":"Write down your top 5 prayer requests. Be specific. Date them. Come back in a month and see how God has moved. This builds your faith history.","journal_prompt_category":"prayer"}', 5, true),
('journal_prompt', 'Letter to Your Future Self', NULL, '{"journal_prompt_text":"Write a letter to yourself one year from now. What do you hope to have accomplished? What do you want to remember about this season?","journal_prompt_category":"vision"}', 5, true),
('journal_prompt', 'Strengths and Gifts', NULL, '{"journal_prompt_text":"What are your top 3 strengths or spiritual gifts? How are you using them for God''s kingdom? If you''re not sure, ask someone close to you.","journal_prompt_category":"identity"}', 5, true),
('journal_prompt', 'The Hardest Season', NULL, '{"journal_prompt_text":"Reflect on the hardest season of your life. How did you see God in it — even if you didn''t see Him at the time? What did you learn?","journal_prompt_category":"reflection"}', 5, true),
('journal_prompt', 'Daily Examen', NULL, '{"journal_prompt_text":"Review your day: When did you feel closest to God? When did you feel furthest? What moment brought you the most joy? The most stress? Talk to God about it.","journal_prompt_category":"reflection"}', 5, true),
('journal_prompt', 'A Person Who Shaped You', NULL, '{"journal_prompt_text":"Write about a person who has significantly shaped your faith. What did they teach you? Have you thanked them? Consider sending them a message today.","journal_prompt_category":"gratitude"}', 5, true),
('journal_prompt', 'Your Fears', NULL, '{"journal_prompt_text":"Name your top 3 fears. For each one, find a Bible verse that speaks directly to that fear. Write the verse out. Fear loses power when confronted with truth.","journal_prompt_category":"faith"}', 5, true),
('journal_prompt', 'If Jesus Were Here', NULL, '{"journal_prompt_text":"If Jesus were physically sitting across from you right now, what would you say to Him? What would you ask? Write the conversation as if He were right there. (He is.)","journal_prompt_category":"prayer"}', 5, true),
('journal_prompt', 'Sabbath Reflection', NULL, '{"journal_prompt_text":"How well are you resting? Rate your rest on a scale of 1-10. What is stealing your peace? What one change could you make to improve your rest this week?","journal_prompt_category":"rest"}', 5, true),
('journal_prompt', 'Acts of Kindness', NULL, '{"journal_prompt_text":"Write about a time someone showed you unexpected kindness. How did it make you feel? Now write about a time YOU showed unexpected kindness. How did that feel?","journal_prompt_category":"gratitude"}', 5, true),
('journal_prompt', 'Your Purpose Statement', NULL, '{"journal_prompt_text":"Try writing a personal purpose statement in one sentence: ''I exist to...'' Don''t overthink it. Let the Holy Spirit guide your pen.","journal_prompt_category":"vision"}', 5, true),
('journal_prompt', 'Scripture Memory', NULL, '{"journal_prompt_text":"Choose one verse to memorise this week. Write it out 5 times right now. By the end of the week, you''ll know it by heart. Start with Philippians 4:13.","journal_prompt_category":"scripture"}', 5, true),
('journal_prompt', 'Highs and Lows', NULL, '{"journal_prompt_text":"What was your HIGH this week? What was your LOW? For the high, thank God. For the low, ask for His perspective. Both are part of your story.","journal_prompt_category":"reflection"}', 5, true);
INSERT INTO cards (type, title, subtitle, content, points_reward, is_active) VALUES
('share_card', 'You Are Loved', NULL, '{"share_mood":"happy","share_text":"Just a reminder: You are deeply, unconditionally, and completely loved. Not because of what you do, but because of who you are. God loves you. And so do I. 💛"}', 5, true),
('share_card', 'Stay Strong', NULL, '{"share_mood":"sad","share_text":"I know things are tough right now, but you are tougher. God is with you in this storm. You''re going to make it through. I''m praying for you. 💪"}', 5, true),
('share_card', 'You''re Not Alone', NULL, '{"share_mood":"lonely","share_text":"Feeling alone? You''re not. I see you. God sees you. You matter more than you know. Reach out — people care about you more than you realise. 🤗"}', 5, true),
('share_card', 'Peace Be With You', NULL, '{"share_mood":"anxious","share_text":"May the peace of God, which transcends all understanding, guard your heart and mind today. Breathe. Trust. He''s got you. ☮️"}', 5, true),
('share_card', 'Grateful for You', NULL, '{"share_mood":"grateful","share_text":"I just want you to know — I''m grateful for you. Your presence in my life is a blessing I don''t take for granted. Thank you for being you. 🙏"}', 5, true),
('share_card', 'It''s Okay to Feel', NULL, '{"share_mood":"angry","share_text":"It''s okay to feel what you''re feeling. God can handle your honesty. Bring it all to Him — the anger, the frustration, the confusion. He understands. ❤️"}', 5, true),
('share_card', 'Sending Light', NULL, '{"share_mood":"sad","share_text":"Sending you light on a dark day. Remember: weeping may last for a night, but joy comes in the morning. Hold on. Morning is coming. ✨"}', 5, true),
('share_card', 'You Inspire Me', NULL, '{"share_mood":"happy","share_text":"Your faith inspires me. The way you keep going, keep trusting, keep believing — it makes me want to do the same. Never stop being you. 🌟"}', 5, true),
('share_card', 'God''s Got a Plan', NULL, '{"share_mood":"anxious","share_text":"When life feels uncertain, remember: God is never uncertain. He knows the plan. He''s working it out. Trust the journey, even when you can''t see the destination. 🗺️"}', 5, true),
('share_card', 'Celebrate You', NULL, '{"share_mood":"happy","share_text":"Today, I celebrate you — your growth, your resilience, your kindness. You''re becoming the person God designed you to be, and it''s beautiful to watch. 🎉"}', 5, true),
('share_card', 'Deep Breath', NULL, '{"share_mood":"anxious","share_text":"Take a deep breath right now. In... and out. God is in control. You don''t have to figure it all out today. One step at a time. You''ve got this — because He''s got you. 🌿"}', 5, true),
('share_card', 'Prayer for You', NULL, '{"share_mood":"sad","share_text":"I prayed for you today. Not because I think you''re broken, but because I know prayer changes things. God is moving on your behalf. Believe it. 🙏"}', 5, true),
('share_card', 'Joy Bomb', NULL, '{"share_mood":"happy","share_text":"Consider this a JOY BOMB: The Creator of the entire universe thinks about you constantly, loves you relentlessly, and is cheering you on right now. Boom. 💥😊"}', 5, true),
('share_card', 'Courage Card', NULL, '{"share_mood":"anxious","share_text":"Be strong and courageous. Do not be afraid; do not be discouraged, for the LORD your God will be with you wherever you go. — Joshua 1:9 🦁"}', 5, true),
('share_card', 'You Matter', NULL, '{"share_mood":"lonely","share_text":"In case no one has told you today: You matter. Your life has meaning. Your story has purpose. Don''t you dare give up. The world needs what you carry. 💎"}', 5, true),
('share_card', 'Thank God for You', NULL, '{"share_mood":"grateful","share_text":"Some people come into your life and make it infinitely better. You''re one of those people. Thank God for putting you in my life. 🌺"}', 5, true),
('share_card', 'It Gets Better', NULL, '{"share_mood":"sad","share_text":"This chapter is hard. But it''s still just a chapter — not the whole book. Better days are coming. Trust the Author. He writes beautiful endings. 📖"}', 5, true),
('share_card', 'Rise Up', NULL, '{"share_mood":"angry","share_text":"Channel that fire into fuel. Let your frustration drive you to pray harder, love deeper, and fight for what matters. You''re a warrior, not a victim. 🔥"}', 5, true),
('share_card', 'Good Vibes Only', NULL, '{"share_mood":"happy","share_text":"''Rejoice in the Lord always. I will say it again: Rejoice!'' — Philippians 4:4. Today is a good day to have a good day. Sending you all the good vibes and God vibes! ☀️"}', 5, true),
('share_card', 'Night Prayer', NULL, '{"share_mood":"lonely","share_text":"As you lay down tonight, know that someone is thinking of you and praying for you. You are covered. You are safe. You are loved. Sleep well. 🌙"}', 5, true);
INSERT INTO cards (type, title, subtitle, content, points_reward, is_active) VALUES
('pause', 'Daily Reset Complete', 'See you tomorrow!', '{"pause_message":"You''ve reached the end of today''s feed. Well done for showing up! Rest, reflect, and come back tomorrow for fresh content.","pause_type":"default","pause_cta_text":"Come back tomorrow ✨"}', 0, true),
('pause', 'Merry Christmas', 'Celebrating the birth of Christ', '{"pause_message":"Glory to God in the highest, and on earth peace, goodwill toward men. Merry Christmas from Soul Sync! 🎄","pause_type":"christmas","pause_cta_text":"Joy to the World!"}', 0, true),
('pause', 'He Is Risen!', 'Happy Easter', '{"pause_message":"The tomb is empty. Death has been defeated. He is risen indeed! Happy Easter from Soul Sync. 🌅","pause_type":"easter","pause_cta_text":"Celebrate the Resurrection!"}', 0, true),
('pause', 'Happy New Year', 'New beginnings, new blessings', '{"pause_message":"Behold, I am doing a new thing! A new year of God''s faithfulness begins today. Happy New Year from Soul Sync! 🎉","pause_type":"new_year","pause_cta_text":"Step into the new year!"}', 0, true),
('pause', 'Time to Rest', 'You''ve done well today', '{"pause_message":"You''ve engaged with today''s content beautifully. Now it''s time to rest. Let what you''ve learned settle into your heart.","pause_type":"default","pause_cta_text":"Rest and return tomorrow"}', 0, true),
('pause', 'Spring Blessings', 'A season of new growth', '{"pause_message":"Just as nature blooms in spring, God is bringing new growth in your life. Enjoy this season of renewal! 🌸","pause_type":"seasonal","pause_cta_text":"Bloom where you''re planted"}', 0, true),
('pause', 'Summer Grace', 'Warmth and light', '{"pause_message":"Long days, warm nights, and endless grace. Enjoy this beautiful season and soak in God''s goodness. ☀️","pause_type":"seasonal","pause_cta_text":"Shine bright!"}', 0, true),
('pause', 'Autumn Reflection', 'A season of letting go', '{"pause_message":"Like the leaves, sometimes we need to let go to make room for new growth. What is God asking you to release? 🍂","pause_type":"seasonal","pause_cta_text":"Let go and grow"}', 0, true),
('pause', 'You Are Enough', 'A gentle reminder', '{"pause_message":"Before you scroll somewhere else — hear this: You are enough. You are loved. You are seen. Come back tomorrow and we''ll remind you again. 💛","pause_type":"motivational","pause_cta_text":"See you tomorrow"}', 0, true),
('pause', 'Gratitude Pause', 'Count your blessings', '{"pause_message":"Before you go, name one thing you''re grateful for right now. Say it out loud. Gratitude changes everything. 🙏","pause_type":"gratitude","pause_cta_text":"Thank you, Lord"}', 0, true);
INSERT INTO cards (type, title, subtitle, content, points_reward, is_active) VALUES
('task', 'Send an Encouragement', 'Brighten someone''s day', '{"description":"Send a text, voice note, or message to someone encouraging them today. Tell them something you appreciate about them. Watch how it lifts both of you."}', 15, true),
('task', 'Read a Chapter', 'Dive into God''s Word', '{"description":"Read one chapter of the Bible today. If you''re not sure where to start, try Psalm 23, John 1, or Romans 8. Take your time and let the words soak in."}', 15, true),
('task', 'Pray for 5 Minutes', 'Dedicated prayer time', '{"description":"Set a timer for 5 minutes and pray without distraction. No phone, no multitasking — just you and God. Talk to Him like a friend. He''s listening."}', 15, true),
('task', 'Write 3 Gratitudes', 'Start a gratitude habit', '{"description":"Write down 3 things you''re grateful for today. Be specific. For example: ''I''m grateful for the sunshine through my window this morning.'' Small thankfulness has big impact."}', 15, true),
('task', 'Random Act of Kindness', 'Spread God''s love practically', '{"description":"Do one unexpected kind thing for someone today. Buy a stranger''s coffee, hold a door, compliment someone, or leave a kind note. Be the hands and feet of Jesus."}', 15, true),
('task', 'Memorise a Verse', 'Hide God''s Word in your heart', '{"description":"Choose one verse and commit it to memory. Write it on a sticky note, set it as your wallpaper, or repeat it throughout the day. Suggested: Philippians 4:13."}', 15, true),
('task', 'Digital Detox Hour', 'Unplug and reconnect', '{"description":"Put your phone down for one full hour. No social media, no messages, no scrolling. Use the time to read, pray, walk, or just be present. Notice how it feels."}', 15, true),
('task', 'Forgive Someone', 'Release the weight', '{"description":"Is there someone you need to forgive? Today, make the choice — even if the feeling hasn''t caught up yet. Say it out loud: ''I choose to forgive [name].'' Freedom follows."}', 15, true),
('task', 'Worship for 10 Minutes', 'Praise without distraction', '{"description":"Play your favourite worship song and give God 10 minutes of undivided praise. Sing, dance, kneel, cry — whatever feels right. Let worship reset your spirit."}', 15, true),
('task', 'Compliment 3 People', 'Speak life today', '{"description":"Give genuine, specific compliments to 3 different people today. Not ''you look nice'' but ''I love how you always make people feel welcome.'' Words of life are powerful."}', 15, true),
('task', 'Walk and Pray', 'Movement + conversation with God', '{"description":"Take a 15-minute walk and use the time to pray. Pray for your neighbourhood as you walk through it. Pray for the people you pass. Let each step be a prayer."}', 15, true),
('task', 'Clean Your Space', 'Outer order, inner calm', '{"description":"Spend 15 minutes tidying your room, desk, or living area. A clean space creates mental clarity. As you organise your surroundings, ask God to organise your heart."}', 15, true),
('task', 'Write a Prayer', 'Put pen to paper', '{"description":"Write out a prayer in your journal or notes app. Something specific, something honest, something from the heart. Written prayers are powerful — they become a record of your faith journey."}', 15, true),
('task', 'Share Your Testimony', 'Tell someone your story', '{"description":"Tell one person today about something God has done in your life — big or small. Your testimony is powerful because it''s yours. No one can argue with what God has done for you."}', 15, true),
('task', 'Fast from Complaining', 'A 24-hour challenge', '{"description":"For the next 24 hours, replace every complaint with gratitude. Instead of ''I hate traffic'' try ''I''m grateful for a car.'' It''s harder than you think — and more transformative."}', 15, true),
('task', 'Listen to a Sermon', 'Feed your spirit', '{"description":"Find a sermon online (10-20 minutes) and listen intentionally. Take one note. Apply one thing. Sometimes the message you need is waiting for you to press play."}', 15, true),
('task', 'Reach Out to Someone Lonely', 'Be the answer to a prayer', '{"description":"Think of someone who might be going through a tough time. Reach out — a call, a text, a visit. You might be the answer to their prayer today."}', 15, true),
('task', 'Donate Something', 'Practice generosity', '{"description":"Find one item you no longer need and give it away — to a charity shop, a friend, or someone in need. Generosity loosens the grip of materialism on your heart."}', 15, true),
('task', 'No Phone First Hour', 'Start your day differently', '{"description":"Tomorrow morning, don''t touch your phone for the first hour after waking up. Instead: pray, read, stretch, eat breakfast mindfully. See how it changes your entire day."}', 15, true),
('task', 'Read Psalm 91', 'A psalm of protection', '{"description":"Read Psalm 91 slowly, out loud if possible. Let every verse wash over you like a shield. This is God''s promise of protection for you. Claim it."}', 15, true);
