-- ============================================
-- MIGRATION: Seed All Content Categories
-- Generated: 2026-02-16
-- Minimum 20 items per feed card type
-- Categories: devotional, prayer, motivational,
--   inspiration, article, fact, riddle, joke,
--   thought_provoking, meme, visual, journal_prompt,
--   share_card, pause, task
-- ============================================

BEGIN;

-- ============================================
-- 0. ENSURE TYPE CONSTRAINT INCLUDES ALL TYPES
-- ============================================
ALTER TABLE cards DROP CONSTRAINT IF EXISTS cards_type_check;
ALTER TABLE cards ADD CONSTRAINT cards_type_check CHECK (
  type IN (
    'verse', 'devotional', 'article', 'quiz', 'game', 'task', 'journal', 'ad',
    'prayer', 'motivational', 'inspiration', 'meme', 'fact', 'riddle', 'joke',
    'thought_provoking', 'visual', 'share_card', 'marketing', 'milestone',
    'upgrade', 'journal_prompt', 'pause'
  )
);

-- ============================================
-- 1. DEVOTIONALS (20 cards)
-- ============================================

-- ============================================
-- 2. PRAYERS (25 cards)
-- ============================================

-- ============================================
-- 3. MOTIVATIONAL (25 cards)
-- ============================================

-- ============================================
-- 4. INSPIRATION (25 cards)
-- ============================================

-- ============================================
-- 5. ARTICLES (20 cards)
-- ============================================

-- ============================================
-- 6. FACTS (25 cards)
-- ============================================

-- ============================================
-- 7. RIDDLES (25 cards)
-- ============================================

-- ============================================
-- 8. JOKES (25 cards)
-- ============================================

-- ============================================
-- 9. THOUGHT-PROVOKING (20 cards)
-- ============================================

-- ============================================
-- 10. MEMES (20 cards)
-- ============================================

-- ============================================
-- 11. VISUAL / CALM CORNER (20 cards)
-- ============================================

-- ============================================
-- 12. JOURNAL PROMPTS (25 cards)
-- ============================================

-- ============================================
-- 13. SHARE CARDS (20 cards)
-- ============================================

-- ============================================
-- 14. PAUSE CARDS (10 cards)
-- ============================================

-- ============================================
-- 15. TASKS (20 cards)
-- ============================================

-- ============================================
-- 16. LINK ALL NEW CARDS TO ALL MOODS
-- ============================================
SELECT c.id, m.id, 1
FROM cards c
CROSS JOIN moods m
WHERE c.is_active = true
  AND m.is_active = true
  AND c.type IN ('devotional', 'prayer', 'motivational', 'inspiration', 'article',
                  'fact', 'riddle', 'joke', 'thought_provoking', 'meme',
                  'visual', 'journal_prompt', 'share_card', 'pause', 'task')
ON CONFLICT (card_id, mood_id) DO NOTHING;

-- ============================================
-- 17. LINK CARDS TO CONTENT CATEGORIES
-- ============================================

-- Link articles to Insights category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'article' AND cat.slug = 'insights'
ON CONFLICT DO NOTHING;

-- Link thought_provoking to Deep Dive category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'thought_provoking' AND cat.slug = 'deep-dive'
ON CONFLICT DO NOTHING;

-- Link facts to Gems category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'fact' AND cat.slug = 'gems'
ON CONFLICT DO NOTHING;

-- Link riddles to Enigmas category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'riddle' AND cat.slug = 'enigmas'
ON CONFLICT DO NOTHING;

-- Link jokes to Light Hearts category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'joke' AND cat.slug = 'light-hearts'
ON CONFLICT DO NOTHING;

-- Link memes to Joy Moments category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'meme' AND cat.slug = 'joy-moments'
ON CONFLICT DO NOTHING;

-- Link visuals to Calm Corner category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'visual' AND cat.slug = 'calm-corner'
ON CONFLICT DO NOTHING;

-- Link journal_prompt to Reflections category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'journal_prompt' AND cat.slug = 'reflections'
ON CONFLICT DO NOTHING;

-- Link share_card to Share Cards category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'share_card' AND cat.slug = 'share-cards'
ON CONFLICT DO NOTHING;

-- Link prayers to Boosts category
SELECT c.id, cat.id
FROM cards c, content_categories cat
WHERE c.type = 'prayer' AND cat.slug = 'boosts'
ON CONFLICT DO NOTHING;

COMMIT;
